using System.Collections;using System.Collections.Generic;using UnityEngine;public class EnemyShip : MonoBehaviour {    public float moveSpeed = 8f;    public float turnSpeed = 0.5f;    public float fireRate = 1.5f;    public float fireRange = 60f;    public float fireTime = 0;    public GameObject bullet;    public Transform muzzlePoint;    public void Update()    {        fireTime += Time.deltaTime;        if (fireTime < fireRate)            return;        if (transform.position.sqrMagnitude > fireRange * fireRange)            return;        // No esta apuntando        if (Vector3.Angle(transform.forward, -transform.position) > 10)            return;        Instantiate(bullet, muzzlePoint.position, muzzlePoint.rotation);        fireTime = 0;    }    public void LateUpdate()    {        float step = turnSpeed * Time.deltaTime;        Quaternion toPlayer = Quaternion.LookRotation(-transform.position);        transform.rotation = Quaternion.Slerp(transform.rotation, toPlayer, step);        transform.position += transform.forward * moveSpeed * Time.deltaTime;        if (transform.position.sqrMagnitude > 1e5)            Destroy(gameObject);    }}